const getResource = async (url) => {
    const res = await fetch(url);

    if (!res.ok) {
        throw new Error(`Its impossible, because we have received ` + `${res.status}`)
    }

    const body = await res.json();
    return body;
}

getResource('https://swapi.co/api/people/5/')
    .then((body) => {
        console.log(body);
    })
    .catch((err) => {
        console.error('It is not possible', err)
    });
