import React, { Component } from 'react';

import SwapiServise from '../../services/swapi-service';

import './random-planet.css';

export default class RandomPlanet extends Component {

  swapiService = new this.swapiService;

  state = {
    name: null,
    population: null,
    rotationPeriod: null,
    diameter: null
  }

  updatePlanet() {
    this.swapiService
      .getPlanet(5)
      .then((item) => {
        this.setState({
          name: item.name,
          population: item.population,
          rotationPerios: item.rotation_period,
          diameter: item.diameter
        })
      })

  }

  render() {
    const {name, population, rotationPeriod, diameter} = this.state;

    return (
      <div className="random-planet jumbotron rounded">
        <img className="planet-image"
             src="https://starwars-visualguide.com/assets/img/planets/5.jpg" />
        <div>
          <h4>{name}</h4>
          <ul className="list-group list-group-flush">
            <li className="list-group-item">
              <span className="term">{population}</span>
              <span>123124</span>
            </li>
            <li className="list-group-item">
              <span className="term">{rotationPeriod}</span>
              <span>43</span>
            </li>
            <li className="list-group-item">
              <span className="term">{diameter}</span>
              <span>100</span>
            </li>
          </ul>
        </div>
      </div>

    );
  }
}
